$(function(){
  //get dom elem
  var $width=$('#width'),
      $height=$('#height'),
      $btuCal=$('#calculate'),
      $perimeter=$('#perimeter'),
      $area= $('#area');
  /*calc button click event */
$btuCal.click(function(){
    //get value
    var w=$width.val();
    var h=$height.val();
    //calculate
    var r = new Rectangle(w, h);
    //output
    $perimeter.val(r.perimeter());
    $area.val(r.area());
});
});



function Rectangle(wid, hei) {

  var w = Number(wid),
      h = Number(hei);

    this.area = function() {
        return retain(w*h,2);
            
    };

    this.perimeter = function() {
        return retain(2*(w+h),2);
            
    };

}
function retain(s,n){
    return Math.round(s * Math.pow(10, n)) / Math.pow(10, n);

}
